SELECT    
IP_PERSON.PERSON_NBR AS MAIN_OWNER_PERSON_NBR,    IP_PERSON.PERSON_NAME AS MAIN_OWNER_PERSON_NAME,   IP_PERSON.NATIONALITY_COUNTRY_CODE AS MAIN_OWNER_COUNTRY_CODE,     CF_GEO_COUNTRY.NATIONALITY AS MAIN_OWNER_NATIONALITY FROM IP_PERSON  LEFT JOIN CF_GEO_COUNTRY ON IP_PERSON.NATIONALITY_COUNTRY_CODE = CF_GEO_COUNTRY.COUNTRY_CODE 