SELECT COUNT (*) FROM TX_TM_GENERAL 
	JOIN M_STATUS ON TX_TM_GENERAL.STATUS_ID = M_STATUS.STATUS_CODE
	
	WHERE 1 = 1 
	AND
	TX_TM_GENERAL.GROUP_JOURNAL = 0
	AND  
	TX_TM_GENERAL.APPLICATION_ID NOT IN (SELECT TX_GROUP_DETAIL.APPLICATION_ID FROM TX_GROUP_DETAIL 
	WHERE TX_GROUP_DETAIL.GROUP_ID IN (SELECT TX_GROUP.GROUP_ID FROM TX_GROUP WHERE TX_GROUP.GROUP_TYPE='GrupPublikasi')  )
	AND
	M_STATUS.STATUS_ID NOT IN (
		'IPT_DRAFT' ,
		'TM_PERMOHONAN_BARU' ,
		'IPT_PENGAJUAN_PERMOHONAN' ,
